import { Component, OnInit } from '@angular/core';
import { Product } from '../models/product.model';
import { CartService } from '../service/cart.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent implements OnInit {
  cart: Product[] = [];

  constructor(private cartService: CartService) { }

  ngOnInit(): void {
    this.cart = this.cartService.getCart();
  }

  onRemoveProduct(product: Product): void {
    this.cartService.removeProduct(product);
  }

  onIncreaseQuantity(product: Product): void {
    const currentQuantity = this.cartService.getQuantity(product);
    this.cartService.setQuantity(product, currentQuantity + 1);
  }

  onDecreaseQuantity(product: Product): void {
    const currentQuantity = this.cartService.getQuantity(product);
    if(currentQuantity == 1){
      this.cartService.removeProduct(product);


    }
    if (currentQuantity > 0) {
      this.cartService.setQuantity(product, currentQuantity - 1);
      
    }
 
  }
  getCartItems() {
    return this.cartService.getCart();
}

  getProductQuantity(product: Product): number {
    return this.cartService.getQuantity(product);
  }
  getTotalPrice(): number {
    return this.cartService.getTotalPrice();
    
  }
}
<div class="container mt-5">
    <h2>Your Shopping Cart</h2>
    <table class="table table-striped mt-3">
        <thead>
            <tr>
                <th>Product Name</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total</th>
                <th>Actions</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let product of getCartItems()">
                <td>{{ product.productName }}</td>
                <td>{{ product.price }}</td>
                <td>{{ getProductQuantity(product) }}</td>
                <td>{{ (product.price * getProductQuantity(product)) | roundDown }}</td>
                <td>
                    <button class="btn btn-primary" (click)="onIncreaseQuantity(product)">Increase Quantity</button>
                    <button class="btn btn-secondary" (click)="onDecreaseQuantity(product)">Decrease Quantity</button>
                    <button class="btn btn-danger" (click)="onRemoveProduct(product)">Remove from cart</button>
                </td>
            </tr>
            <tr>
                <td colspan="3" class="text-right"><strong>Total Cost</strong></td>
                <td colspan="2"><strong>{{ alertProductQuantities() }}</strong></td>
            </tr>
        </tbody>
    </table>
    <button *ngIf="getTotalPrice() > 0" routerLink="/checkout" class="btn btn-success mt-3">Checkout</button>

</div>
